mapper.select:
select

  distinct

'true' as QUERYID,

from prod_catalog_inv_facility

  


  order by ${orderByClause}


  limit ${limitStart} , ${pageSize}

select 

from prod_catalog_inv_facility
where PROD_CATALOG_ID = #{prodCatalogId,jdbcType=VARCHAR}
  and FACILITY_ID = #{facilityId,jdbcType=VARCHAR}
  and FROM_DATE = #{fromDate,jdbcType=TIMESTAMP}

mapper.delete:
delete from prod_catalog_inv_facility
where PROD_CATALOG_ID = #{prodCatalogId,jdbcType=VARCHAR}
  and FACILITY_ID = #{facilityId,jdbcType=VARCHAR}
  and FROM_DATE = #{fromDate,jdbcType=TIMESTAMP}

mapper.update:
update prod_catalog_inv_facility

  
    THRU_DATE = #{thruDate,jdbcType=TIMESTAMP},
  
  
    SEQUENCE_NUM = #{sequenceNum,jdbcType=DECIMAL},
  
  LAST_UPDATED_STAMP =  NOW() ,
  LAST_UPDATED_TX_STAMP =  NOW() ,

where PROD_CATALOG_ID = #{prodCatalogId,jdbcType=VARCHAR}
  and FACILITY_ID = #{facilityId,jdbcType=VARCHAR}
  and FROM_DATE = #{fromDate,jdbcType=TIMESTAMP}

update prod_catalog_inv_facility
set THRU_DATE = #{thruDate,jdbcType=TIMESTAMP},
  SEQUENCE_NUM = #{sequenceNum,jdbcType=DECIMAL},
  LAST_UPDATED_STAMP =  NOW() ,
  LAST_UPDATED_TX_STAMP =  NOW() ,
where PROD_CATALOG_ID = #{prodCatalogId,jdbcType=VARCHAR}
  and FACILITY_ID = #{facilityId,jdbcType=VARCHAR}
  and FROM_DATE = #{fromDate,jdbcType=TIMESTAMP}
