mapper.select:
select

  distinct

'true' as QUERYID,

from product_category_gl_account

  


  order by ${orderByClause}


  limit ${limitStart} , ${pageSize}

select 

from product_category_gl_account
where PRODUCT_CATEGORY_ID = #{productCategoryId,jdbcType=VARCHAR}
  and ORGANIZATION_PARTY_ID = #{organizationPartyId,jdbcType=VARCHAR}
  and GL_ACCOUNT_TYPE_ID = #{glAccountTypeId,jdbcType=VARCHAR}

mapper.delete:
delete from product_category_gl_account
where PRODUCT_CATEGORY_ID = #{productCategoryId,jdbcType=VARCHAR}
  and ORGANIZATION_PARTY_ID = #{organizationPartyId,jdbcType=VARCHAR}
  and GL_ACCOUNT_TYPE_ID = #{glAccountTypeId,jdbcType=VARCHAR}

mapper.update:
update product_category_gl_account

  
    GL_ACCOUNT_ID = #{glAccountId,jdbcType=VARCHAR},
  
  LAST_UPDATED_STAMP =  NOW() ,
  LAST_UPDATED_TX_STAMP =  NOW() ,

where PRODUCT_CATEGORY_ID = #{productCategoryId,jdbcType=VARCHAR}
  and ORGANIZATION_PARTY_ID = #{organizationPartyId,jdbcType=VARCHAR}
  and GL_ACCOUNT_TYPE_ID = #{glAccountTypeId,jdbcType=VARCHAR}

update product_category_gl_account
set GL_ACCOUNT_ID = #{glAccountId,jdbcType=VARCHAR},
  LAST_UPDATED_STAMP =  NOW() ,
  LAST_UPDATED_TX_STAMP =  NOW() ,
where PRODUCT_CATEGORY_ID = #{productCategoryId,jdbcType=VARCHAR}
  and ORGANIZATION_PARTY_ID = #{organizationPartyId,jdbcType=VARCHAR}
  and GL_ACCOUNT_TYPE_ID = #{glAccountTypeId,jdbcType=VARCHAR}
