mapper.select:
select

  distinct

'true' as QUERYID,

from product_config_option_iactn

  


  order by ${orderByClause}


  limit ${limitStart} , ${pageSize}

select 

from product_config_option_iactn
where CONFIG_ITEM_ID = #{configItemId,jdbcType=VARCHAR}
  and CONFIG_OPTION_ID = #{configOptionId,jdbcType=VARCHAR}
  and CONFIG_ITEM_ID_TO = #{configItemIdTo,jdbcType=VARCHAR}
  and CONFIG_OPTION_ID_TO = #{configOptionIdTo,jdbcType=VARCHAR}
  and SEQUENCE_NUM = #{sequenceNum,jdbcType=DECIMAL}

mapper.delete:
delete from product_config_option_iactn
where CONFIG_ITEM_ID = #{configItemId,jdbcType=VARCHAR}
  and CONFIG_OPTION_ID = #{configOptionId,jdbcType=VARCHAR}
  and CONFIG_ITEM_ID_TO = #{configItemIdTo,jdbcType=VARCHAR}
  and CONFIG_OPTION_ID_TO = #{configOptionIdTo,jdbcType=VARCHAR}
  and SEQUENCE_NUM = #{sequenceNum,jdbcType=DECIMAL}

mapper.update:
update product_config_option_iactn

  
    CONFIG_IACTN_TYPE_ID = #{configIactnTypeId,jdbcType=VARCHAR},
  
  
    DESCRIPTION = #{description,jdbcType=VARCHAR},
  
  LAST_UPDATED_STAMP =  NOW() ,
  LAST_UPDATED_TX_STAMP =  NOW() ,

where CONFIG_ITEM_ID = #{configItemId,jdbcType=VARCHAR}
  and CONFIG_OPTION_ID = #{configOptionId,jdbcType=VARCHAR}
  and CONFIG_ITEM_ID_TO = #{configItemIdTo,jdbcType=VARCHAR}
  and CONFIG_OPTION_ID_TO = #{configOptionIdTo,jdbcType=VARCHAR}
  and SEQUENCE_NUM = #{sequenceNum,jdbcType=DECIMAL}

update product_config_option_iactn
set CONFIG_IACTN_TYPE_ID = #{configIactnTypeId,jdbcType=VARCHAR},
  DESCRIPTION = #{description,jdbcType=VARCHAR},
  LAST_UPDATED_STAMP =  NOW() ,
  LAST_UPDATED_TX_STAMP =  NOW() ,
where CONFIG_ITEM_ID = #{configItemId,jdbcType=VARCHAR}
  and CONFIG_OPTION_ID = #{configOptionId,jdbcType=VARCHAR}
  and CONFIG_ITEM_ID_TO = #{configItemIdTo,jdbcType=VARCHAR}
  and CONFIG_OPTION_ID_TO = #{configOptionIdTo,jdbcType=VARCHAR}
  and SEQUENCE_NUM = #{sequenceNum,jdbcType=DECIMAL}
