mapper.select:
select

  distinct

'true' as QUERYID,

from product_feature_group_appl

  


  order by ${orderByClause}


  limit ${limitStart} , ${pageSize}

select 

from product_feature_group_appl
where PRODUCT_FEATURE_GROUP_ID = #{productFeatureGroupId,jdbcType=VARCHAR}
  and PRODUCT_FEATURE_ID = #{productFeatureId,jdbcType=VARCHAR}
  and FROM_DATE = #{fromDate,jdbcType=TIMESTAMP}

mapper.delete:
delete from product_feature_group_appl
where PRODUCT_FEATURE_GROUP_ID = #{productFeatureGroupId,jdbcType=VARCHAR}
  and PRODUCT_FEATURE_ID = #{productFeatureId,jdbcType=VARCHAR}
  and FROM_DATE = #{fromDate,jdbcType=TIMESTAMP}

mapper.update:
update product_feature_group_appl

  
    THRU_DATE = #{thruDate,jdbcType=TIMESTAMP},
  
  
    SEQUENCE_NUM = #{sequenceNum,jdbcType=DECIMAL},
  
  LAST_UPDATED_STAMP =  NOW() ,
  LAST_UPDATED_TX_STAMP =  NOW() ,

where PRODUCT_FEATURE_GROUP_ID = #{productFeatureGroupId,jdbcType=VARCHAR}
  and PRODUCT_FEATURE_ID = #{productFeatureId,jdbcType=VARCHAR}
  and FROM_DATE = #{fromDate,jdbcType=TIMESTAMP}

update product_feature_group_appl
set THRU_DATE = #{thruDate,jdbcType=TIMESTAMP},
  SEQUENCE_NUM = #{sequenceNum,jdbcType=DECIMAL},
  LAST_UPDATED_STAMP =  NOW() ,
  LAST_UPDATED_TX_STAMP =  NOW() ,
where PRODUCT_FEATURE_GROUP_ID = #{productFeatureGroupId,jdbcType=VARCHAR}
  and PRODUCT_FEATURE_ID = #{productFeatureId,jdbcType=VARCHAR}
  and FROM_DATE = #{fromDate,jdbcType=TIMESTAMP}
